import React from 'react'
import './Table.css'

var $TABLE = $('#table');
var $BTN = $('#export-btn');
var $EXPORT = $('#export');

$('.table-add').click(function () {
var $clone = $TABLE.find('tr.hide').clone(true).removeClass('hide table-line');
$TABLE.find('table').append($clone);
});

$('.table-remove').click(function () {
$(this).parents('tr').detach();
});

$('.table-up').click(function () {
var $row = $(this).parents('tr');
if ($row.index() === 1) return; // Don't go above the header
$row.prev().before($row.get(0));
});

$('.table-down').click(function () {
var $row = $(this).parents('tr');
$row.next().after($row.get(0));
});

// A few jQuery helpers for exporting only
jQuery.fn.pop = [].pop;
jQuery.fn.shift = [].shift;

$BTN.click(function () {
var $rows = $TABLE.find('tr:not(:hidden)');
var headers = [];
var data = [];

// Get the headers (add special header logic here)
$($rows.shift()).find('th:not(:empty)').each(function () {
headers.push($(this).text().toLowerCase());
});

// Turn all existing rows into a loopable array
$rows.each(function () {
var $td = $(this).find('td');
var h = {};

// Use the headers from earlier to name our hash keys
headers.forEach(function (header, i) {
h[header] = $td.eq(i).text();
});

data.push(h);
});

// Output the result
$EXPORT.text(JSON.stringify(data));
});

const Table = () => {
  return (
    <div className="card">
        <h3 className="card-header text-center font-weight-bold text-uppercase py-4">Investments</h3>
        <div className="card-body">
        <div id="table" className="table-editable">
            <span className="table-add float-right mb-3 mr-2"><a href="#!" className="text-success"><i className="fas fa-plus fa-2x" aria-hidden="true" /></a></span>
            <table className="table table-bordered table-responsive-md table-striped text-center">
            <tbody>
                <tr>
                <th className="text-center">Person Name</th>
                <th className="text-center">Age</th>
                <th className="text-center">Company Name</th>
                <th className="text-center">Country</th>
                <th className="text-center">City</th>
                <th className="text-center">Sort</th>
                <th className="text-center">Remove</th>
                </tr>
                {/* This is our clonable table line */}
                <tr>
                <td className="pt-3-half" contentEditable="true">Guerra Cortez</td>
                <td className="pt-3-half" contentEditable="true">45</td>
                <td className="pt-3-half" contentEditable="true">Insectus</td>
                <td className="pt-3-half" contentEditable="true">USA</td>
                <td className="pt-3-half" contentEditable="true">San Francisco</td>
                <td className="pt-3-half">
                    <span className="table-up"><a href="#!" className="indigo-text"><i className="fas fa-long-arrow-alt-up" aria-hidden="true" /></a></span>
                    <span className="table-down"><a href="#!" className="indigo-text"><i className="fas fa-long-arrow-alt-down" aria-hidden="true" /></a></span>
                </td>
                <td>
                    <span className="table-remove"><button type="button" className="btn btn-danger btn-rounded btn-sm my-0">Remove</button></span>
                </td>
                </tr>
            </tbody>
        </table>
        </div>
        </div>
    </div>
  )
}

export default Table
