import React from 'react'
import './AddInvestmentForm.css'  
// import formData from './formData.json'

const AddInvestmentForm = props => {
       const { handleChange, handleSubmit, inputs: { purchaseDate, name, symbol, amountPurchased, purchasePrice } } = props 
       return(
              <form className='investment-form' onSubmit={handleSubmit}> 
                     <span className='entry1'>Entry:</span>
                     <input type="date" 
                            id="purchaseDate" 
                            name="purchaseDate"
                            value={purchaseDate}
                            onChange={handleChange}
                            min="2009-01-01" max="2099-12-31"
                     />
                     <select className='symbol-selector' 
                             onChange={handleChange} 
                             name='name'
                             value={name}
                             placeholder='Name' >
                            <option value="Bitcoin">Bitcoin</option>
                            <option value="Ethereum">Ethereum</option>
                            <option value="Bitcoin Cash">Bitcoin Cash</option>
                            <option value="Litecoin">Litecoin</option>
                     </select>

                     {/* <input type='text' 
                            name='name'
                            value={name}
                            onChange={handleChange} 
                            placeholder='Name'
                     />    */}
                     <select className='symbol-selector' 
                             onChange={handleChange} 
                             name='name'
                             value={symbol} >
                            <option value="BTC">BTC</option>
                            <option value="ETH">ETH</option>
                            <option value="BCH">BCH</option>
                            <option value="LTC">LTC</option>
                     </select>
                     {/* <input type='text' 
                            name='symbol'
                            value={symbol}
                            onChange={handleChange} 
                            placeholder='Symbol'
                     />    */}
                     <input type='number' 
                            name='amountPurchased'
                            value={amountPurchased}
                            onChange={handleChange} 
                            placeholder='Amount Purchased'
                     />   
                     <input type='number' step="0.00000001"
                            name='purchasePrice'
                            value={purchasePrice}
                            onChange={handleChange} 
                            placeholder='Purchase Price'
                     />
                     <button>Submit</button>
              </form>
       )
}

export default AddInvestmentForm
